import { isBand } from './utils/_controlTypes';
export function getNearestBand(target) {
    var bandModel = null;
    var model = target;
    do {
        if (isBand(model.controlType))
            bandModel = model;
        model = model.parentModel();
    } while (!bandModel && model);
    return bandModel;
}
