import { ajax, getErrorMessage, ShowMessage } from '@devexpress/analytics-core/analytics-internal';
import { HandlerUri } from '../utils/settings';
export class ControlConverterService {
    static getXmlStringFromJson(controlJsonLayout, doneCallback, errorCallback) {
        return ajax(HandlerUri(), 'jsonToXmlString', encodeURIComponent(JSON.stringify({ 'XRControlSerializer': { 'Controls': { 'Item1': controlJsonLayout } } })))
            .done((result) => doneCallback(result))
            .fail((error) => {
            var message = getErrorMessage(error);
            message && ShowMessage(message);
            errorCallback(error);
        });
    }
    static getControlModelFromXmlString(controlXmlLayout, doneCallback, errorCallback) {
        return ajax(HandlerUri(), 'xmlStringToJson', encodeURIComponent(controlXmlLayout))
            .done((result) => doneCallback(JSON.parse(result).XRControlSerializer.Controls.Item1))
            .fail((error) => {
            var message = getErrorMessage(error);
            message && ShowMessage(message);
            errorCallback(error);
        });
    }
}
